function categorise(context,patient){ return "Possible";
}

context.result = categorise(context, patient);

var height = context.CDEHeight;
var weight = context.CDEWeight;

context.result = weight / (height * height);

var flexor = parseInt(context.FlexorDigitorumProfundis);
var tibialis = parseInt(context.TibialisAnterior);
var neck = parseInt(context.NeckFlexion);
var iliopsoas = parseInt(context.Iliopsoas);

context.result = 0;

if(flexor) { context.result  += flexor; }
if(tibialis) { context.result  += tibialis; }
if(neck) { context.result  += neck; }
if(iliopsoas) { context.result  += iliopsoas; }
